async function fetchDailySignals(limit = 7) {
  const res = await fetch(`/api/daily-signals?limit=${limit}`);
  if (!res.ok) throw new Error(`daily-signals ${res.status}`);
  const data = await res.json();
  return Array.isArray(data.signals) ? data.signals : [];
}

code_snippets.render_tape:
function renderTape(signals) {
  const root = document.getElementById('live-tape-items');
  if (!root) return;
  if (!signals.length) {
    root.innerHTML = '<div class="tape-empty">No verified signals available. <a href="/daily-signals">View All Signals</a></div>';
    return;
  }
  root.innerHTML = signals.map((s) => `
    <article class="tape-item" data-id="${s.id}">
      <time class="tape-date">${new Date(s.timestamp_utc).toISOString().slice(5, 10)}</time>
      <button class="tape-headline" type="button">${s.headline}</button>
      <div class="tape-meta">${s.entity} · ${s.index} ${s.delta > 0 ? '+' : ''}${s.delta} · ${s.confidence}</div>
    </article>`).join('');
}

code_snippets.render_weekly_brief_preview:
function renderLatestWeeklyBrief(brief) {
  const root = document.getElementById('latest-weekly-brief');
  if (!root) return;
  if (!brief) {
    root.innerHTML = '<div class="brief-empty">Weekly brief unavailable.</div>';
    return;
  }
  root.innerHTML = `
    <h3>${brief.title}</h3>
    <p>${brief.subtitle}</p>
    <ul>${brief.executive_delta_summary.slice(0,3).map((x)=>`<li>${x}</li>`).join('')}</ul>
    <a href="/weekly-briefs/${brief.report_id}">Read weekly brief</a>`;
}

